import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, TouchableOpacity, Button, Text, View, Picker } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  titles: {
    fontSize: 30
  },

  settings: {
    marginTop: 30,
  },

  buttons: {
    flexDirection: "row",
    marginLeft: 20, 
    justifyContent: 'space-evenly'
  },

  select: {
    marginTop: 30
  }
});

export default class App extends React.Component {
    constructor(props) {
        super(props);
        let workTime = 25 * 60;
        let pauseTime = 5 * 60;
        this.state = {workTime : workTime, pauseTime : pauseTime, timeLast: workTime, isWorkTime: true, isTimerStarted: false, selectedValue: "25:5"}

        setInterval(() => {
            if (this.state.isTimerStarted) {
                if (this.state.timeLast <= 1) {
                    if (this.state.isWorkTime) {
                        this.setState(prevState => ({timeLast: prevState.pauseTime}));
                    } else {
                        this.setState(prevState => ({timeLast: prevState.workTime}));
                    }

                    this.setState(prevState => ({isWorkTime: !prevState.isWorkTime}));

                    // Sound when changing
                    let beep = new Audio("data:audio/wav;base64,");  
                    beep.play();
                } else {
                    this.setState(prevState => ({timeLast: prevState.timeLast - 1}));
                }
            }
        }, 1000);
    }

    displayTimer = () => {
        const timeLast = this.state.timeLast;
        let minutes = parseInt(timeLast / 60, 10);
        let seconds = parseInt(timeLast % 60, 10);

        minutes = minutes < 10 ? "0" + minutes : minutes
        seconds = seconds < 10 ? "0" + seconds : seconds    
        
        return minutes + ":" + seconds;
    }

    onClickStartTimer = () => {
        this.setState({isTimerStarted: true});
    }

    onClickStopTimer = () => {
        this.setState({isTimerStarted: false});
    }

    onClickResetTimer = () => {
        this.setState({isTimerStarted: false, timeLast: this.state.workTime, isWorkTime: true});
    }

    onClickSelectTimer = (itemValue) => {
        switch(itemValue) {
            case "25:5" :
                this.setState({workTime : 25 * 60, pauseTime : 5 * 60, isTimerStarted: false, timeLast: 25 * 60, isWorkTime: true, selectedValue: "25:5"});
                break;
            case "45:10" :
                this.setState({workTime : 45 * 60, pauseTime : 10 * 60, isTimerStarted: false, timeLast: 45 * 60, isWorkTime: true, selectedValue: "45:10"});
                break;
            default :
                break; 
        }
    }

    render() {
        return(
            <View style={styles.container}>
              <Text style={this.state.isWorkTime ? {color: "red", fontSize: 40} : {color: "green", fontSize: 40}}>{this.state.isWorkTime ? "Travail" : "Pause"}</Text>
              <Text style={this.state.timeLast <= 20 ? {color: "red", fontSize: 30} : {color: "black", fontSize: 30}}>{this.displayTimer()}</Text>
              
              <View style={styles.settings}>
                <View style={styles.buttons}>
                  <Button onPress={this.onClickStartTimer} title="START"/>
                  <Button onPress={this.onClickStopTimer} title="STOP"/>
                  <Button onPress={this.onClickResetTimer} title="RESET"/>
                </View>
                <View style={styles.select}>
                  <Text>Choose a timer : </Text>
                  <Picker
                    selectedValue={this.state.selectedValue}
                    onValueChange={(itemValue, itemIndex) => this.onClickSelectTimer(itemValue)}
                  >
                    <Picker.Item label="25 minutes of work for 5 minutes of pause" value="25:5" />
                    <Picker.Item label="45 minutes of work for 10 minutes of pause" value="45:10" />
                  </Picker>
                </View>
              </View>
              <StatusBar style="auto" />
            </View>
        );
    }
}